package looping;

public class WhileExample {

	public static void main(String[] args) {
	// TODO Auto-generated method stub
    int i=0;
	while(i<5)
	{
		System.out.println("statement::"+i);
		i++;
	}
	 int j=0;
	do 
	{
		System.out.println("statement :: "+j);
	     j++;
	}
		while(j<5);
	
		
	}

}
